import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Index = () => {
  const [startRange, setStartRange] = useState('');
  const [endRange, setEndRange] = useState('');
  const [primeNumbers, setPrimeNumbers] = useState([]);
  const [algorithmType, setAlgorithmType] = useState('');
  const [primeCount, setPrimeCount] = useState(0);
  const [primeRecords, setPrimeRecords] = useState([]);



  const handleclick = async () => {
    try {
      handleGenerate();
      const response = await axios.post('http://localhost:3001/prime-records', { // Specify port 3001
        start: startRange,
        end: endRange,
        algorithm: algorithmType,
        numPrimes: primeCount,
      });
      console.log(response);
     
    } catch (error) {
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.log('Server Error:', error.response.data);
      } else if (error.request) {
        // The request was made but no response was received
        console.log('No response received from server:', error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.log('Error:', error.message);
      }
    }
  };
  
  
  

  const handleGenerate = async () => {
    const start = parseInt(startRange);
    const end = parseInt(endRange);

    if (isNaN(start) || isNaN(end) || start >= end) {
      alert('Please enter valid start and end range!');
      return;
    }

    const primes = [];
    let count = 0;

    for (let i = start; i <= end; i++) {
      if (isPrime(i)) {
        primes.push(i);
        count++;
      }
    }

    setPrimeNumbers(primes);
    setPrimeCount(count);
  };

  const isPrime = (num) => {
    if (num <= 1) return false;
    if (num <= 3) return true;

    if (num % 2 === 0 || num % 3 === 0) return false;

    let i = 5;
    while (i * i <= num) {
      if (num % i === 0 || num % (i + 2) === 0) return false;
      i += 6;
    }

    return true;
  };

  return (
    <div>
      <form>
        <div>
          <label>Enter the starting range:</label>
          <input type="number" name="start" value={startRange} onChange={(e) => setStartRange(e.target.value)} />
        </div>
        <div>
          <label>Enter the ending range:</label>
          <input type="number" name="end" value={endRange} onChange={(e) => setEndRange(e.target.value)} />
        </div>
        <div>
          <label>Enter the algorithm type:</label>
          <input type="text" name="algorithm" value={algorithmType} onChange={(e) => setAlgorithmType(e.target.value)} />
        </div>
        <button type="button" onClick={() => handleclick()}>
          Generate
        </button>
      </form>
      <div>
        <h2>Prime numbers between {startRange} and {endRange}:</h2>
        <p>Algorithm used: {algorithmType}</p>
        <p>Count of prime numbers: {primeCount}</p>
        <ul>
          {primeNumbers.map((num, index) => (
            <li key={index}>{num}</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Prime Records:</h2>
        <ul>
          {primeRecords.map((record) => (
            <li key={record._id}>{record.start} - {record.end}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Index;
